    return (
        <Container fluid={true}>
            <Row>
                <Col md={6}>
                    <div>
                        <div>
                            Размер шрифта:
                        </div>
                        <div>
                            <Form.Control value={fontSize} onChange={(event)=>{setFontSize(event.target.value)}} type={'number'} className={'w-auto search'}/>
