                    </div>
                    <div className={'mt-5 text-center'}>
                        <div>
                            Выберите тему:
                        </div>
                        <div className={'d-flex flex-row'}>
                            <div>Темная тема</div>
                                <Form.Check
                                    onChange={(e)=>{
                                        setLightChange(!lightChange);
                                    }}
                                    type="switch"
                                    id="custom-switch"

                                />
                            <div>Светлая тема</div>
                        </div>

                    </div>

                </Col>
            </Row>
        </Container>
    );
};

export default SettingPage;
