const initialState = {
    country: 'ru',
    from: "2022-04-03T21:00:00.000Z",
    to: new Date().toISOString(),
    pageSize: 100,
    page:1
}

export default function criteriaReducer(state = initialState, action) {
    console.log(action)
    switch (action.type) {
        case 'CHANGE_CRITERIA': {
            return {
                ...state,
                country: 'ru',
                pageSize: 100,
                page:1,
                from: "2022-04-03T21:00:00.000Z",
                to: action.payload.to,

            }
        }
        default:
            return state
    }
}
