const initialState = {
    country: 'ru',
    from: new Date(2022, 3, 7).toISOString(),
    to: new Date().toISOString(),
    pageSize: 20,
    page:1
}

export default function criteriaReducer(state = initialState, action) {
    switch (action.type) {
        case 'CHANGE_CRITERIA': {
            return {
                ...state,
                country: 'ru',
                from: new Date(2022, 3, 11).toISOString(),
                to: new Date().toISOString(),
                pageSize: 20,
                page:1
            }
        }
        default:
            return state
    }
}
