import './App.css';
import Header from "./Components/Header";
import 'bootstrap/dist/css/bootstrap.min.css';
import Filter from "./Components/Filter";
import {Button, Container, Row} from "react-bootstrap";
import {fetchNews} from "./HTTP/NewsAPI";
import {useDispatch, useSelector} from "react-redux";
import {BrowserRouter, Route, Routes} from "react-router-dom";
import MainPage from "./Pages/mainPage";
import {useEffect} from "react";
import SettingPage from "./Pages/settingPage";
import logo from './Static_images/logo.png';
import logoForDark from './Static_images/logoForDark.png';
function App  () {
    const dispatcher = useDispatch();
    const store = useSelector((state)=> {
        return state
    })
    const localSetting = JSON.parse(localStorage.getItem("settings"));
    if (localSetting){
        console.log(localSetting)

    }
  return (
    <Container fluid={true} style={{fontSize:`${store.setting.font}pt`}} className={store.setting.light}>
        <Header light={store.setting.light} image = {logo} />

        <BrowserRouter>
            <Routes>
                <Route path={'/'} element={<MainPage/>}/>
                <Route path={'/setting'} element={<SettingPage/>}/>
            </Routes>

        </BrowserRouter>
    </Container>
  );
}

export default App;
