import React, {useEffect, useState} from 'react';
import {Col, Container, Form, Row} from "react-bootstrap";
import {useDispatch} from "react-redux";

const SettingPage = () => {
    const dispatcher = useDispatch();
    const [fontSize, setFontSize] = useState(14);
    const [lightState, setLightState] = useState('light')
    const [pageSize, setPageSize] = useState(20);

    useEffect(()=>{
        dispatcher({type: 'CHANGE_SETTING', payload: {light: lightState, font: fontSize}})

    }, [fontSize, lightState])
    return (
        <Container fluid={true}>
            <Row>
                <Col md={12}>
                    <div className={'mt-5 text-center d-flex justify-content-center align-items-center flex-column'}>
                        <div>
                            Размер шрифта:
                        </div>
                        <div>
                            <Form.Control value={fontSize} onChange={(event)=>{setFontSize(event.target.value)}} type={'number'} className={'w-auto search'}/>
                        </div>
                    </div>
                    <div className={'mt-5 text-center d-flex justify-content-center align-items-center flex-column'}>
                        <div>
                            Выберите тему:
                        </div>
                        <div className={'d-flex flex-row'}>
                            <div className={'me-4'}>Светлая тема</div>
                                <Form.Check
                                    onChange={()=>{
                                        if (lightState == 'dark'){
                                            setLightState('light');
                                        }else{
                                            setLightState('dark');
                                        }
                                    }}
                                    type="switch"
                                    id="custom-switch"

                                />
                            <div className={'ms-4 '}>Темная тема</div>
                        </div>

                    </div>
                    <div className={'mt-5 text-center d-flex justify-content-center align-items-center flex-column'}>
                        <div>
                            Выберите колличество новостей на странице:
                        </div>
                        <div>
                            <Form.Control value={pageSize} onChange={(event)=>{if((pageSize ))setPageSize(event.target.value)}} type={'number'}  className={'w-auto search'}/>
                        </div>
                    </div>

                </Col>
            </Row>
        </Container>
    );
};

export default SettingPage;
