const initialState = {

    page:1
}

export default function criteriaReducer(state = initialState, action) {
    console.log(action)
    switch (action.type) {
        case 'CHANGE_CRITERIA': {
            return {
                ...state,
                page: action.payload.page
            }
        }
        default:
            return state
    }
}
