const initialState = {
    country: 'ru',
    from: new Date(2022, 3, 7).toISOString(),
    to: new Date().toISOString(),
    pageSize: 20,
    page:1
}

export default function criteriaReducer(state = initialState, action) {
    switch (action.type) {
        case 'CHOISE_CRITERIA': {
            return {
                ...state,
                news: {
                    auth: action.payload.auth,
                    token:action.payload.token
                }
            }
        }
        default:
            return state
    }
}
