import React, {useState} from 'react';
import {Nav, Navbar, Row, NavDropdown, Container, Button, Col} from "react-bootstrap";
import Calendar from "./CalendarFolder/Calendar";
import FilterItem from "./filterItem";
import {useDispatch, useSelector} from "react-redux";
import {fetchNews} from "../HTTP/NewsAPI";

const Filter = () => {
    const [light, setLight] = useState('light')

    const changeLight = ()=>{
        if (light == 'light'){
            setLight('dark');
        }else {
            setLight('light')
        }
    }
    const dispatcher = useDispatch();
    const criteriaStore = useSelector((state)=> {
        return state.criteria
    })
    const newsStore = useSelector((state)=> {
        return state.news
    })
    const fetchN = ()=>{
        fetchNews(criteriaStore).then((data)=>{
            dispatcher({type: 'ADD_NEWS', payload: data.articles})
        })
    }
    return (
        <Row className={'filter'}>
            <Navbar variant={light} bg={light} expand="lg">
                <Row>
                    <Col md={3}>
                        <FilterItem component={<Calendar func={()=>{}}/>} title={'Время от:'} />
                    </Col>
                    <Col md={3}>
                        <FilterItem component={<Calendar func={()=>{}}/>} title={'Время до:'} />
                    </Col>

                </Row>
            </Navbar>

        </Row>

    );
};

export default Filter;
