const Calendar = () => {
    const week = ['ПН','ВТ','СР','ЧТ','ПТ','СБ','ВС',];
    let day = new Date().getDate();
    let dayName = new Date().getDay();
    let start = ()=>{
        let i;
        let j;
        let weeks = Math.floor(day / 7);

        if (weeks >= 1){
            j = dayName;
            i = day - (weeks * 7);
            for (let r = 0; r < i-1; r++){
                if (j < 0){
                    j = 6;
                }
                console.log(j)
                j -= 1;
            }
            return j
        }

    }
    console.log(start())
    return (
        <table className="calendar">
            <thead>
