import React, {useState} from 'react';

const FilterItem = ({component, title}) => {
    const [ display, setDisplay] = useState('d-none')
    return (
        <div onClick={()=>{if (display == 'd-none'){setDisplay('d-block')}else {setDisplay('d-none')}}}>
            <div>{title}</div>
            <div className={'filterItemDropdown'}>
                {component}
            </div>

        </div>
    );
};

export default FilterItem;
