const localStorage = localStorage.getItem();
const initialState = {
    light: 'light',
    font: 12,
    pageSize: 20
}

export default function settingReducer(state = initialState, action) {
    switch (action.type) {
        case 'CHANGE_SETTING': {
            return {
                light: action.payload.light,
                font: action.payload.font,
                pageSize: action.payload.pageSize
            }
        }
        default:
            return state
    }
}
