import React, {useEffect, useState} from 'react';
import {Col, Container, Form, Row} from "react-bootstrap";
import {useDispatch} from "react-redux";

const SettingPage = () => {
    const dispatcher = useDispatch();
    const [fontSize, setFontSize] = useState(14);
    const [lightChange, setLightChange] = useState(true);
    const [pageSize, setPageSize] = useState(true);
    useEffect(()=>{
        dispatcher({type: 'CHANGE_SETTING', payload: {light: 'light', font: fontSize}})
    }, [fontSize, lightChange])
    return (
        <Container fluid={true}>
            <Row>
                <Col md={12}>
                    <div className={'mt-5 text-center d-flex justify-content-center align-items-center flex-column'}>
                        <div>
                            Размер шрифта:
                        </div>
                        <div>
                            <Form.Control value={fontSize} onChange={(event)=>{setFontSize(event.target.value)}} type={'number'} className={'w-auto search'}/>
                        </div>
                    </div>
                    <div className={'mt-5 text-center d-flex justify-content-center align-items-center flex-column'}>
                        <div>
                            Выберите тему:
                        </div>
                        <div className={'d-flex flex-row'}>
                            <div className={'me-4 '}>Темная тема</div>
                                <Form.Check
                                    onChange={(e)=>{
                                        setLightChange(!lightChange);
                                    }}
                                    type="switch"
                                    id="custom-switch"

                                />
                            <div className={'ms-4 '}>Светлая тема</div>
                        </div>

                    </div>

                </Col>
            </Row>
        </Container>
    );
};

export default SettingPage;
