import React, {useState} from 'react';
import {Nav, Navbar, Row, NavDropdown, Container, Button, Col} from "react-bootstrap";
import Calendar from "./CalendarFolder/Calendar";
import FilterItem from "./filterItem";
import {useDispatch, useSelector} from "react-redux";
import {fetchNews} from "../HTTP/NewsAPI";

const Filter = () => {
    const [light, setLight] = useState('light')

    const changeLight = ()=>{
        if (light == 'light'){
            setLight('dark');
        }else {
            setLight('light')
        }
    }
    const dispatcher = useDispatch();
    const criteriaStore = useSelector((state)=> {
        return state.criteria
    })

    const cahgeDateFrom = (date)=>{
        let currentCriteria = criteriaStore;
        dispatcher({type: 'CHANGE_CRITERIA', payload: {}})
    }
    console.log()(criteriaStore)
    const cahgeDateTo = (date)=>{
        let currentCriteria = criteriaStore;
        currentCriteria.to = date
        dispatcher({type: 'CHANGE_CRITERIA', payload: {}})
    }
    return (
        <Row className={'filter'}>
            <Navbar variant={light} bg={light} expand="lg">
                <Row>
                    <Col md={3}>
                        <FilterItem component={<Calendar func={()=>{cahgeDateFrom()}}/>} value={'from'} title={'Время от:'} />
                    </Col>
                    <Col md={3}>
                        <FilterItem component={<Calendar func={()=>{cahgeDateTo()}}/>} value={'to'} title={'Время до:'} />
                    </Col>

                </Row>
            </Navbar>

        </Row>

    );
};

export default Filter;
