import './App.css';
import Header from "./Components/Header";
import 'bootstrap/dist/css/bootstrap.min.css';
import Filter from "./Components/Filter";
import {Button, Container, Row} from "react-bootstrap";
import {fetchNews} from "./HTTP/NewsAPI";
import {useDispatch, useSelector} from "react-redux";
import {BrowserRouter, Route, Routes} from "react-router-dom";
import MainPage from "./Pages/mainPage";
import {useEffect} from "react";
import SettingPage from "./Pages/settingPage";
import logo from './Static_images/logo.png';
import logoForDark from './Static_images/logoForDark.png';
function App  () {
    const dispatcher = useDispatch();
    const criteriaStore = useSelector((state)=> {
        console.log(state)
        console.log('state')
        return state.criteria
    })
    const settingStore = useSelector((state)=> {
        return state.setting
    })
    const fetchN = ()=>{

        fetchNews(criteriaStore).then((data)=>{
            dispatcher({type: 'ADD_NEWS', payload: data.articles})
        })
    }
    useEffect(()=>{
        fetchN();
    }, [])

  return (
    <Container fluid={true} style={{fontSize:`${settingStore.font}pt`}} className={settingStore.light}>
        <Header light={settingStore.light} image = {logo} />

        <BrowserRouter>
            <Routes>
                <Route path={'/'} element={<MainPage/>}/>
                <Route path={'/setting'} element={<SettingPage/>}/>
            </Routes>

        </BrowserRouter>
    </Container>
  );
}

export default App;
