const initialState = {
    news: []
}

export default function newsReducer(state = initialState, action) {
    switch (action.type) {
        case 'FETCH_NEWS': {
            return {
                ...state,
                news: action.payload
            }
        }
        case 'ADD_NEWS': {
            return {
                ...state,
                news: state.news + action.payload
            }
        }
        default:
            return state
    }
}
