import React, {useEffect, useState} from 'react';
import {useDispatch, useSelector} from "react-redux";
import NewCard from "../Components/NewCard";
import {fetchNews} from "../HTTP/NewsAPI";
import Preloader from "../Components/Preloader";

const MainPage = () => {

    const dispatcher = useDispatch();
    const store = useSelector((state)=> {
        return state
    })
    const fetchN = ()=>{
        fetchNews(store.criteria).then((data)=>{
            dispatcher({type: 'ADD_NEWS', payload: data.articles})
        })
    }

    useEffect(()=>{
        fetchN()
    }, [])
    return (
        <>
            {store.news.news.length?<Preloader />:store.news.news.map((item, index)=>{
                return <NewCard key={index} New={item}/>
            })}
            {}
        </>
    );
};

export default MainPage;
