const initialState = {
    country: 'ru',
    pageSize: 100,
    page:1
}

export default function criteriaReducer(state = initialState, action) {
    console.log(action)
    switch (action.type) {
        case 'CHANGE_CRITERIA': {
            return {
                ...state,
                country: action.payload.country,
                pageSize: action.payload.pageSize,
                page: action.payload.page
            }
        }
        default:
            return state
    }
}
