import axios from "axios";
// "https://newsapi.org/v2/top-headlines?country=ru&apiKey=ed0b5d92959f4ad5ac3f43af3002d252"
const host = axios.create({
    baseURL: 'https://newsapi.org/v2/'

});

export const fetchNews = async ({country, pageSize, page})=>{
    const {data} = await host.get('top-headlines', {
        params: {
            country: country,
            pageSize: pageSize,
            page: page,
            apiKey: 'eb6bd0b431a54210b4b1b8ed75831b24',
        }
    } );
    return data
}
